#!python

def compute(s, hard=False):
    floor = 0
    p = 1
    for c in s:
        if c == ')':
            floor -= 1
        elif c == '(':
            floor += 1
        if hard:
            if floor < 0:
                return p
        p += 1
    return floor

assert compute('(())') == 0
assert compute('()()') == 0
assert compute('(((') == 3
assert compute('))(((((') == 3
assert compute(')())())') == -3

assert compute(')', True) == 1
assert compute('()())', True) == 5

